// src/app/api/v1/builds/[buildId]/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { prisma } from '@/lib/prisma';
import { getServerSession } from '@/lib/auth';

const buildIdSchema = z.object({
  buildId: z.string().min(1),
});

// GET /api/v1/builds/[buildId] - Get single build
export async function GET(request: NextRequest, context: { params: Promise<{ buildId: string }> }) {
  try {
    const session = await getServerSession();
    if (!session) {
      return NextResponse.json(
        { error: { code: 'AUTH_UNAUTHORIZED', message: 'Authentication required' } },
        { status: 401 }
      );
    }

    const { buildId } = await context.params;

    const build = await prisma.build.findFirst({
      where: {
        id: buildId,
        deletedAt: null,
      },
    });

    if (!build) {
      return NextResponse.json(
        { error: { code: 'BUILD_NOT_FOUND', message: 'Build not found' } },
        { status: 404 }
      );
    }

    return NextResponse.json({ data: build });

  } catch (error) {
    console.error('Error getting build:', error);
    return NextResponse.json(
      { error: { code: 'INTERNAL_ERROR', message: 'Failed to get build' } },
      { status: 500 }
    );
  }
}

