import { z } from 'zod';
import { prisma } from '@/lib/prisma';
import { getServerSession } from '@/lib/auth';

// Zod schemas for this endpoint
const listBuildsQuerySchema = z.object({
  page: z.coerce.number().int().positive().default(1),
  pageSize: z.coerce.number().int().min(1).max(100).default(20),
  status: z.enum(['QUEUED', 'RUNNING', 'COMPLETED', 'FAILED']).optional(),
});

// GET /api/v1/builds - List builds
export async function GET(request: NextRequest) {
  try {
    // 1. Auth check
    const session = await getServerSession();
    if (!session) {
      return NextResponse.json(
        { error: { code: 'AUTH_UNAUTHORIZED', message: 'Authentication required' } },
        { status: 401 }
      );
    }

    // 2. Parse and validate query params
    const searchParams = Object.fromEntries(request.nextUrl.searchParams);
    const query = listBuildsQuerySchema.safeParse(searchParams);

    if (!query.success) {
      return NextResponse.json(
        { error: { code: 'VALIDATION_FAILED', message: 'Invalid query parameters', details: query.error.flatten() } },
        { status: 422 }
      );
    }

    const { page, pageSize, status } = query.data;

    // 3. Build where clause
    const where = {
      deletedAt: null,
      ...(status && { status }),
    };

    // 4. Execute query with pagination
    const [builds, total] = await Promise.all([
      prisma.build.findMany({
        where,
        skip: (page - 1) * pageSize,
        take: pageSize,
        orderBy: { createdAt: 'desc' },
      }),
      prisma.build.count({ where }),
    ]);

    // 5. Return response envelope
    return NextResponse.json({
      data: builds,
      meta: {
        page,
        pageSize,
        total,
        hasMore: page * pageSize < total,
      },
    });

  } catch (error) {
    console.error('Error listing builds:', error);
    return NextResponse.json(
      { error: { code: 'INTERNAL_ERROR', message: 'Failed to list builds' } },
      { status: 500 }
    );
  }
}

