// src/app/api/builds/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { prisma } from '@/lib/prisma';
import { getServerSession } from '@/lib/auth';
import { errorResponse, validationError } from '@/lib/api-utils';

// Zod schemas for this endpoint
const listBuildsQuerySchema = z.object({
  page: z.coerce.number().int().positive().default(1),
  pageSize: z.coerce.number().int().min(1).max(100).default(20),
});

// GET /api/builds - List builds
export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession();
    if (!session) {
      return errorResponse('AUTH_UNAUTHORIZED', 'Authentication required', 401);
    }

    // Parse and validate query params
    const searchParams = Object.fromEntries(request.nextUrl.searchParams);
    const query = listBuildsQuerySchema.safeParse(searchParams);

    if (!query.success) {
      return validationError(query.error);
    }

    const { page, pageSize } = query.data;

    // Fetch builds from the database
    const builds = await prisma.build.findMany({
      where: { deletedAt: null },
      skip: (page - 1) * pageSize,
      take: pageSize,
      orderBy: { createdAt: 'desc' },
    });

    const total = await prisma.build.count({ where: { deletedAt: null } });

    // Return response
    return NextResponse.json({
      data: builds,
      meta: {
        page,
        pageSize,
        total,
        hasMore: page * pageSize < total,
      },
    });

  } catch (error) {
    console.error('Error listing builds:', error);
    return NextResponse.json(
      { error: { code: 'INTERNAL_ERROR', message: 'Failed to list builds' } },
      { status: 500 }
    );
  }
}

// src/app/api/builds/[buildId]/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { prisma } from '@/lib/prisma';
import { getServerSession } from '@/lib/auth';
import { notFoundError, errorResponse } from '@/lib/api-utils';

const buildIdSchema = z.string().uuid();

// GET /api/builds/[buildId] - Get build details
export async function GET(request: NextRequest, { params }: { params: { buildId: string } }) {
  try {
    const session = await getServerSession();
    if (!session) {
      return errorResponse('AUTH_UNAUTHORIZED', 'Authentication required', 401);
    }

    // Validate buildId
    const parsed = buildIdSchema.safeParse(params.buildId);
    if (!parsed.success) {
      return notFoundError('Build');
    }

    const build = await prisma.build.findFirst({
      where: { id: parsed.data, deletedAt: null },
    });

    if (!build) {
      return notFoundError('Build');
    }

    return NextResponse.json({ data: build });

  } catch (error) {
    console.error('Error getting build details:', error);
    return NextResponse.json(
      { error: { code: 'INTERNAL_ERROR', message: 'Failed to get build details' } },
      { status: 500 }
    );
  }
}

// src/app/api/ai/agents/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { getServerSession } from '@/lib/auth';
import { errorResponse } from '@/lib/api-utils';

// GET /api/ai/agents - Get agent registry
export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession();
    if (!session) {
      return errorResponse('AUTH_UNAUTHORIZED', 'Authentication required', 401);
    }

    const agents = await prisma.agent.findMany({
      where: { deletedAt: null },
      orderBy: { name: 'asc' },
    });

    return NextResponse.json({ data: agents });

  } catch (error) {
    console.error('Error fetching agents:', error);
    return NextResponse.json(
      { error: { code: 'INTERNAL_ERROR', message: 'Failed to fetch agents' } },
      { status: 500 }
    );
  }
}

