{
  "openapi": "3.0.3",
  "info": {
    "title": "OLYMPUS Builder Dashboard API",
    "version": "1.0.0",
    "description": "API documentation for the OLYMPUS Builder Dashboard"
  },
  "servers": [
    { "url": "/api/v1", "description": "API v1" }
  ],
  "paths": {
    "/builds": {
      "get": {
        "operationId": "listBuilds",
        "summary": "List all builds",
        "tags": ["Builds"],
        "responses": {
          "200": {
            description: "List of builds",
            content: {
              "application/json": {
                schema: {
                  type: "object",
                  properties: {
                    data: {
                      type: "array",
                      items: { $ref: "#/components/schemas/Build" }
                    },
                    meta: {
                      type: "object",
                      properties: {
                        page: { type: "integer" },
                        pageSize: { type: "integer" },
                        total: { type: "integer" },
                        hasMore: { type: "boolean" },
                        cursor: { type: "string" }
                      }
                    }
                  }
                }
              }
            }
          },
          "401": { "$ref": "#/components/responses/Unauthorized" }
        }
      }
    },
    "/builds/{buildId}": {
      parameters: [
        {
          name: "buildId",
          in: "path",
          required: true,
          schema: { type: "string" }
        }
      ],
      "get": {
        "operationId": "getBuildDetails",
        "summary": "Get build details by ID",
        "tags": ["Builds"],
        "responses": {
          "200": {
            description: "Build details",
            content: {
              "application/json": {
                schema: {
                  type: "object",
                  properties: {
                    data: { $ref: "#/components/schemas/BuildDetails" }
                  }
                }
              }
            }
          },
          "404": { "$ref": "#/components/responses/NotFound" }
        }
      }
    },
    "/builds/{buildId}/stream": {
      parameters: [
        {
          name: "buildId",
          in: "path",
          required: true,
          schema: { type: "string" }
        }
      ],
      "get": {
        "operationId": "streamBuildUpdates",
        "summary": "Get real-time updates for a build",
        "tags": ["Builds"],
        "responses": {
          "200": {
            description: "Stream of build updates",
            content: {
              "text/event-stream": {
                schema: {
                  type: "string"
                }
              }
            }
          },
          "404": { "$ref": "#/components/responses/NotFound" }
        }
      }
    },
    "/ai/agents": {
      "get": {
        "operationId": "listAgents",
        "summary": "Get agent registry",
        "tags": ["Agents"],
        "responses": {
          "200": {
            description: "List of agents",
            content: {
              "application/json": {
                schema: {
                  type: "object",
                  properties: {
                    data: {
                      type: "array",
                      items: { $ref: "#/components/schemas/Agent" }
                    }
                  }
                }
              }
            }
          },
          "401": { "$ref": "#/components/responses/Unauthorized" }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Build": {
        "type": "object",
        "properties": {
          "id": { "type": "string" },
          "status": { "type": "string", "enum": ["QUEUED", "RUNNING", "COMPLETED", "FAILED"] },
          "timestamp": { "type": "string", "format": "date-time" },
          "duration": { "type": "integer" }
        },
        "required": ["id", "status", "timestamp"]
      },
      "BuildDetails": {
        "type": "object",
        "properties": {
          "id": { "type": "string" },
          "status": { "type": "string", "enum": ["QUEUED", "RUNNING", "COMPLETED", "FAILED"] },
          "phases": {
            type: "array",
            items: {
              type: "object",
              properties: {
                name: { type: "string" },
                status: { type: "string", enum: ["PENDING", "ACTIVE", "COMPLETED", "FAILED"] },
                startTime: { type: "string", format: "date-time" },
                endTime: { type: "string", format: "date-time" },
                explanation: { type: "string" }
              }
            }
          },
          "agents": {
            type: "array",
            items: { $ref: "#/components/schemas/Agent" }
          }
        },
        "required": ["id", "status", "phases"]
      },
      "Agent": {
        "type": "object",
        "properties": {
          "name": { "type": "string" },
          "role": { "type": "string" },
          "status": { "type": "string", "enum": ["IDLE", "WORKING", "COMPLETED", "FAILED"] },
          "currentTask": { type: "string", nullable: true }
        },
        "required": ["name", "role", "status"]
      }
    },
    "responses": {
      "Unauthorized": {
        description: "Unauthorized",
        content: {
          "application/json": {
            schema: {
              type: "object",
              properties: {
                error: {
                  type: "object",
                  properties: {
                    code: { type: "string", example: "AUTH_UNAUTHORIZED" },
                    message: { type: "string", example: "Not authenticated" }
                  }
                }
              }
            }
          }
        }
      },
      "NotFound": {
        description: "Resource not found",
        content: {
          "application/json": {
            schema: {
              type: "object",
              properties: {
                error: {
                  type: "object",
                  properties: {
                    code: { type: "string", example: "BUILD_NOT_FOUND" },
                    message: { type: "string", example: "Build not found" }
                  }
                }
              }
            }
          }
        }
      }
    }
  },
  "endpoints_summary": [
    {
      "method": "GET",
      "path": "/api/v1/builds",
      "auth": "required",
      "description": "List all builds"
    },
    {
      "method": "GET",
      "path": "/api/v1/builds/{buildId}",
      "auth": "required",
      "description": "Get build details by ID"
    },
    {
      "method": "GET",
      "path": "/api/v1/builds/{buildId}/stream",
      "auth": "required",
      "description": "Get real-time updates for a build"
    },
    {
      "method": "GET",
      "path": "/api/v1/ai/agents",
      "auth": "required",
      "description": "Get agent registry"
    }
  ],
  "error_codes": [
    { "code": "BUILD_NOT_FOUND", "status": 404, "message": "Build not found" },
    { "code": "AUTH_UNAUTHORIZED", "status": 401, "message": "Not authenticated" }
  ]
}

