import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { prisma } from '@/lib/prisma';
import { getServerSession } from '@/lib/auth';
import { notFoundError, errorResponse } from '@/lib/api-utils';

// GET /api/v1/builds/[buildId] - Get single build details
export async function GET(request: NextRequest, context: { params: Promise<{ buildId: string }> }) {
  try {
    const session = await getServerSession();
    if (!session) {
      return errorResponse('AUTH_UNAUTHORIZED', 'Authentication required', 401);
    }

    const { buildId } = await context.params;

    const build = await prisma.build.findFirst({
      where: { id: buildId, deletedAt: null },
    });

    if (!build) {
      return notFoundError('Build');
    }

    return NextResponse.json({ data: build });
  } catch (error) {
    console.error('Error getting build details:', error);
    return errorResponse('INTERNAL_ERROR', 'Failed to get build details', 500);
  }
}

// src/app/api/v1/builds/[buildId]/stream/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { getServerSession } from '@/lib/auth';

// GET /api/v1/builds/[buildId]/stream - SSE for real-time updates
export async function GET(request: NextRequest, context: { params: Promise<{ buildId: string }> }) {
  try {
    const session = await getServerSession();
    if (!session) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { buildId } = await context.params;
    // Implement SSE logic for real-time updates
    // This is a placeholder; real implementation will depend on your logic
    return NextResponse.json({ message: 'Streaming updates for buildId: ' + buildId });
  } catch (error) {
    console.error('Error streaming updates:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

