{
  "tech_stack": {
    "framework": "Next.js 14 App Router",
    "database": "Supabase PostgreSQL",
    "orm": "Prisma",
    "auth": "Supabase Auth",
    "cache": "Upstash Redis",
    "state": "Zustand",
    "hosting": "Vercel",
    "styling": "Tailwind CSS + shadcn/ui",
    "icons": "Lucide React",
    "animation": "Framer Motion",
    "validation": "Zod"
  },
  "architecture": {
    "pattern": "monolith",
    "reasoning": "The OLYMPUS Builder Dashboard is a focused MVP with clear core features and less than 10 entities, making a monolithic architecture suitable for reducing complexity and speeding up development.",
    "scalability": "small",
    "estimated_entities": 7,
    "estimated_api_routes": 5
  },
  "database": {
    "softDeletes": true,
    "auditTimestamps": true,
    "idStrategy": "cuid",
    "cascadeDeletes": false,
    "auditedTables": [
      "builds",
      "agents",
      "constitutional_checks"
    ],
    "reasoning": "Soft deletes allow for recovery of builds and agents if needed. Audit timestamps help track changes. Using cuid for IDs ensures uniqueness and ease of sorting."
  },
  "multiTenancy": {
    "enabled": false,
    "isolation": "none",
    "tenantScopedTables": [],
    "globalTables": [],
    "tenantResolution": "session",
    "rowLevelSecurity": false,
    "reasoning": "The application is a single-tenant MVP aimed at power users, so multi-tenancy is not required at this stage."
  },
  "api": {
    "basePath": "/api/v1",
    "pagination": "offset",
    "defaultPageSize": 20,
    "maxPageSize": 100,
    "responseEnvelope": true,
    "rateLimiting": {
      "enabled": true,
      "tiers": {
        "free": {
          "requestsPerHour": 100
        },
        "pro": {
          "requestsPerHour": 1000
        },
        "enterprise": {
          "requestsPerHour": 10000
        }
      }
    },
    "reasoning": "Using offset pagination is sufficient given the expected small dataset size. Rate limiting is enabled to prevent abuse of the API endpoints."
  },
  "auth": {
    "mfaRequired": [],
    "mfaOptional": [],
    "accessTokenDuration": 3600,
    "refreshTokenDuration": 604800,
    "maxConcurrentSessions": 3,
    "rbac": {
      "enabled": true,
      "roles": [
        {
          "name": "user",
          "permissions": [
            "read:builds"
          ]
        },
        {
          "name": "admin",
          "permissions": [
            "read:builds",
            "manage:agents",
            "view:constitutional_checks"
          ]
        }
      ]
    },
    "reasoning": "No MFA required for simplicity and to enhance user experience. The roles defined allow for clear permissions without over-complicating access control."
  },
  "cache": {
    "provider": "upstash",
    "cachedEntities": [
      "builds"
    ],
    "cacheDuration": {
      "builds": 300
    },
    "invalidationStrategy": "time-based",
    "reasoning": "Caching builds reduces load times when retrieving build lists, as they are not frequently modified."
  },
  "state": {
    "optimisticUpdates": false,
    "hydration": "client",
    "persistedStores": [
      "ui_preferences"
    ],
    "reasoning": "Optimistic updates are not critical for this read-only interface. Client hydration ensures that state is consistent with the server."
  },
  "standards": {
    "typescript": "strict",
    "eslint": "next/recommended",
    "prettier": true,
    "testing": "vitest + @testing-library/react",
    "naming": {
      "components": "PascalCase",
      "functions": "camelCase",
      "constants": "SCREAMING_SNAKE_CASE",
      "files": "kebab-case"
    }
  },
  "reasoning": "The architecture is designed to support a focused MVP that prioritizes clarity and usability for power users. All decisions align with the technical constraints and design requirements, ensuring a streamlined development process."
}

