import useSWR from 'swr';

async function fetcher(url: string) {
  const res = await fetch(url);
  if (!res.ok) {
    throw new Error('Failed to fetch');
  }
  return res.json();
}

export function useBuilds() {
  const { data, error } = useSWR('/api/builds', fetcher);
  return {
    data,
    error,
    isLoading: !error && !data
  };
}

