// Token service for managing tokens
import { useState, useEffect } from 'react';

const TokenService = () => {
  const [tokens, setTokens] = useState<number>(5);
  const [prompt, setPrompt] = useState<string>('');

  const startBuilding = (prompt: string) => {
    setPrompt(prompt);
    setTokens(5);
  };

  const useToken = () => {
    if (tokens > 0) {
      setTokens(tokens - 1);
      return true;
    }
    return false;
  };

  return { startBuilding, useToken, tokens };
};

export default TokenService;

// Build service for managing builds
import axios from 'axios';

const BuildService = () => {
  const startBuild = async (prompt: string) => {
    try {
      const response = await axios.post('/api/guest/build', { prompt });
      return response.data;
    } catch (error) {
      console.error(error);
      return null;
    }
  };

  const checkTokens = async () => {
    try {
      const response = await axios.get('/api/guest/build');
      return response.data;
    } catch (error) {
      console.error(error);
      return null;
    }
  };

  return { startBuild, checkTokens };
};

export default BuildService;

// Auth service for managing authentication
import axios from 'axios';

const AuthService = () => {
  const login = async (email: string, password: string) => {
    try {
      const response = await axios.post('/api/login', { email, password });
      return response.data;
    } catch (error) {
      console.error(error);
      return null;
    }
  };

  const signup = async (name: string, email: string, password: string) => {
    try {
      const response = await axios.post('/api/signup', { name, email, password });
      return response.data;
    } catch (error) {
      console.error(error);
      return null;
    }
  };

  return { login, signup };
};

export default AuthService;

// API routes for guest builds
import { NextApiRequest, NextApiResponse } from 'next';

const buildApi = async (req: NextApiRequest, res: NextApiResponse) => {
  if (req.method === 'POST') {
    // Start build
    const { prompt } = req.body;
    // Call build service
    const buildService = new BuildService();
    const buildData = await buildService.startBuild(prompt);
    if (buildData) {
      return res.json(buildData);
    }
    return res.status(500).json({ message: 'Error starting build' });
  }

  if (req.method === 'GET') {
    // Check tokens
    const buildService = new BuildService();
    const tokensData = await buildService.checkTokens();
    if (tokensData) {
      return res.json(tokensData);
    }
    return res.status(500).json({ message: 'Error checking tokens' });
  }

  return res.status(405).json({ message: 'Method not allowed' });
};

export default buildApi;

// Hook for using tokens
import { useState, useEffect } from 'react';
import TokenService from './tokenService';

const useTokenHook = () => {
  const tokenService = new TokenService();
  const [tokens, setTokens] = useState<number>(tokenService.tokens);

  const useToken = () => {
    if (tokens > 0) {
      setTokens(tokens - 1);
      return true;
    }
    return false;
  };

  return { useToken, tokens };
};

export default useTokenHook;

