const resend = new Resend(process.env.RESEND_API_KEY);

export async function sendEmail(to: string, templateId: string, variables: Record<string, string>) {
  const template = await getTemplate(templateId);
  const html = interpolate(template.html, variables);
  return resend.emails.send({ from: 'noreply@olympus.com', to, subject: interpolate(template.subject, variables), html });
}

import { useNotification } from '@notifications';

export async function sendInAppNotification(message: string, variables: Record<string, string>) {
  const notification = await useNotification();
  notification.show(message, variables);
}

import { Push } from 'push-api';

const push = new Push(process.env.PUSH_API_KEY);

export async function sendPushNotification(message: string, variables: Record<string, string>) {
  return push.send({ message, variables });
}

