import React, { useEffect, useState } from 'react';
import { useLocalStorage } from 'react-use';
import Button from '@/components/ui/button';

const BuilderPage: React.FC = () => {
  const [tokens, setTokens] = useLocalStorage<number>('olympus_tokens', 5);
  const [sessionId, setSessionId] = useState<string | null>(null);
  const [progress, setProgress] = useState<string>('Idle');

  const startBuild = async (prompt: string) => {
    const response = await fetch('/api/guest/build', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt, sessionId }),
    });
    const data = await response.json();
    setSessionId(data.sessionId);
    setTokens(data.tokens);
    // Handle additional data...
  };

  const checkTokens = async () => {
    if (!sessionId) return;
    const response = await fetch(\`/api/guest/build?sessionId=\${sessionId}\`);
    const data = await response.json();
    setTokens(data.tokens);
    if (data.tokens === 0) {
      alert('Out of tokens! Sign up to get 50 more.'); // Trigger signup prompt
    }
  };

  useEffect(() => {
    const interval = setInterval(checkTokens, 5000);
    return () => clearInterval(interval);
  }, [sessionId]);

  return (
    <div className="flex flex-col h-screen">
      <header className="fixed top-0 left-0 right-0 h-14 flex justify-between items-center p-4">
        <div>Project Name | Tokens: {tokens}</div>
        <div>
          <Button onClick={() => startBuild(prompt)}>Share</Button>
          <Button>Deploy</Button>
        </div>
      </header>
      <main className="flex-1 flex">
        {/* File Explorer, Editor, AI Chat components go here */}
      </main>
    </div>
  );
};

export default BuilderPage;
`
