import React from 'react';

interface ButtonProps {
  variant?: 'primary' | 'secondary' | 'ghost' | 'outline';
  onClick?: () => void;
  children: React.ReactNode;
  className?: string;
}

const Button: React.FC<ButtonProps> = ({ variant = 'primary', onClick, children, className }) => {
  const baseStyle = 'px-4 py-2 rounded transition duration-200';
  const variantStyle = variant === 'primary' ? 'bg-violet-600 text-white' : 'bg-gray-600 text-white';

  return (
    <button className={`${baseStyle} ${variantStyle} ${className}`} onClick={onClick}>
      {children}
    </button>
  );
};

export default Button;
`
