    {
      "description": "Create a minimum viable product (MVP) without extensive coding knowledge",
      "importance": "Medium",
      "frequency": "Weekly"
    },
    {
      "description": "Reduce development costs and increase profit margins",
      "importance": "Low",
      "frequency": "Monthly"
    }
  ],
  "user_journey": [
    {
      "step": "Landing page",
      "action": "Enter prompt and click 'Start Building'",
      "expectation": "Redirect to builder page with 5 free tokens"
    },
    {
      "step": "Builder page",
      "action": "Interact with AI assistant and build app",
      "expectation": "See build progress and receive notifications when tokens are low"
