{
  "tech_stack": {
    "framework": "Next.js 14 App Router",
    "database": "Supabase PostgreSQL",
    "orm": "Prisma",
    "auth": "Supabase Auth",
    "cache": "Upstash Redis",
    "state": "Zustand",
    "hosting": "Vercel",
    "styling": "Tailwind CSS + shadcn/ui"
  },
  "architecture": {
    "pattern": "monolith",
    "reasoning": "The application's core features can be built as a single, cohesive unit, with clear boundaries between the landing page, builder, and authentication flows. A monolithic architecture simplifies development and maintenance, allowing for faster iteration and deployment.",
    "scalability": "medium"
  },
  "multiTenancy": {
    "enabled": false,
    "isolation": "none",
    "tenantScopedTables": [],
    "globalTables": [],
    "tenantResolution": "none",
    "rowLevelSecurity": false
  },
  "database": {
    "softDeletes": true,
    "auditTimestamps": true,
    "idStrategy": "cuid",
    "cascadeDeletes": false,
    "auditedTables": [
      "users",
      "builds"
    ]
  },
  "api": {
    "basePath": "/api/v1",
    "pagination": "cursor",
    "responseEnvelope": true,
    "rateLimiting": {
      "enabled": true,
      "tiers": {
        "guest": 10,
        "authenticated": 50
      }
    }
  },
  "auth": {
    "mfaRequired": [
      "admin"
    ],
    "sessionDuration": 604800,
    "rbac": {
