import React, { useState } from 'react';
import { useRouter } from 'next/router';

const HomePage = () => {
  const [prompt, setPrompt] = useState('');
  const router = useRouter();

  const handleStartBuilding = () => {
    if (prompt) {
      localStorage.setItem('olympus_initial_prompt', prompt);
      localStorage.setItem('olympus_tokens', '5');
      router.push('/builder');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-r from-purple-500 to-blue-500">
      <div className="text-center">
        <h1 className="text-5xl font-bold gradient-text">Build anything. <span className="text-7xl">50X faster.</span></h1>
        <p className="mt-4">Platform description goes here.</p>
        <div className="mt-6 p-6 bg-[#0d0d0d] rounded-lg shadow-lg">
          <textarea
            className="textarea resize-none w-full h-32 p-2 bg-[#0d0d0d] border border-white/10 focus:ring-primary"
            placeholder="Describe what you want to build..."
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
          />
          <div className="flex flex-wrap mt-4 gap-2">
            <button className="chip" onClick={() => setPrompt('Build me a Notion clone with dark mode')}>Build me a Notion clone with dark mode</button>
            <button className="chip" onClick={() => setPrompt('Create an e-commerce store for sneakers')}>Create an e-commerce store for sneakers</button>
            <button className="chip" onClick={() => setPrompt('Make a portfolio site with animations')}>Make a portfolio site with animations</button>
            <button className="chip" onClick={() => setPrompt('Build a SaaS dashboard with analytics')}>Build a SaaS dashboard with analytics</button>
          </div>
          <button className="mt-4 bg-primary text-white px-4 py-2 rounded-full w-full" onClick={handleStartBuilding}>Start Building</button>
          <p className="mt-2 text-sm">No signup required. Start now, save later.</p>
        </div>
      </div>
    </div>
  );
};

export default HomePage;

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/router';

const BuilderPage = () => {
  const [tokens, setTokens] = useState(5);
  const router = useRouter();

