import { Resend } from 'resend';

const resend = new Resend(process.env.RESEND_API_KEY);

export async function sendEmail(to: string, templateId: string, variables: Record<string, string>) {
  const template = await getTemplate(templateId);
  const html = interpolate(template.html, variables);
  return resend.emails.send({ from: 'noreply@olympus.com', to, subject: interpolate(template.subject, variables), html });
}

import { useState, useEffect } from 'react';

const InAppNotification = () => {
  const [message, setMessage] = useState('');

  useEffect(() => {
    // fetch notification from server
    const notification = getNotification();
    setMessage(notification.message);
  }, []);

  return <div>{message}</div>;
};

export default InAppNotification;

import { PushAPI } from 'push-api';

const push = new PushAPI(process.env.PUSH_API_KEY);

export async function sendPushNotification(to: string, message: string) {
  return push.sendNotification({ to, message });
}

