// API client hook
import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchApi } from './fetchApi';

const useApi = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const params = useParams();
  const navigate = useNavigate();

  const fetchData = async () => {
    setLoading(true);
    try {
      const response = await fetchApi(params);
      setData(response.data);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [params]);

  return { data, error, loading };
};

export default useApi;

// API fetch function
import axios from 'axios';

const fetchApi = async (params: any) => {
  const url = `/api/${params.id}`;
  const response = await axios.get(url);
  return response;
};

export default fetchApi;

// Auth hook
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { login, signup } from './authApi';

const useAuth = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (credentials: any) => {
    try {
      const response = await login(credentials);
      setUser(response.data);
      navigate('/builder');
    } catch (error) {
      setError(error);
    }
  };

  const handleSignup = async (credentials: any) => {
    try {
      const response = await signup(credentials);
      setUser(response.data);
      navigate('/builder');
    } catch (error) {
      setError(error);
    }
  };

  return { user, error, handleLogin, handleSignup };
};

export default useAuth;

// Auth API functions
import axios from 'axios';

const login = async (credentials: any) => {
  const url = '/api/login';
  const response = await axios.post(url, credentials);
  return response;
};

const signup = async (credentials: any) => {
  const url = '/api/signup';
  const response = await axios.post(url, credentials);
  return response;
};

export { login, signup };

// Builder hook
import { useState, useEffect } from 'react';
import { useApi } from './useApi';
import { useAuth } from './useAuth';

const useBuilder = () => {
  const { data, error, loading } = useApi();
  const { user, error: authError } = useAuth();
  const [tokens, setTokens] = useState(5);

  const handleBuild = async () => {
    // Call API to start build
    const response = await fetchApi({ id: 'build' });
    setTokens(response.data.tokens);
  };

  return { data, error, loading, user, authError, tokens, handleBuild };
};

export default useBuilder;

