{
  "business_model": {
    "type": "Freemium",
    "description": "Offer a basic version of the platform for free, with limitations on features and tokens, and charge for premium features and additional tokens"
  },
  "pricing": {
    "free": {
      "tokens": 5,
      "features": [
        "Limited app builder",
        "Limited AI assistance"
      ]
    },
    "premium": {
      "price": "$29.99/month",
      "tokens": 50,
      "features": [
        "Full app builder",
        "Advanced AI assistance",
        "Priority support"
      ]
    }
  },
  "unit_economics": {
    "customer_acquisition_cost": "$10",
    "average_revenue_per_user": "$29.99/month",
    "customer_lifetime_value": "$179.94",
    "token_cost": "$0.50",
    "gross_margin": "80%"
  },
  "metrics": [
    {
      "name": "Monthly Active Users",
      "target": 10000
    },
    {
      "name": "Conversion Rate (Free to Premium)",
      "target": 10
    },
