// src/app/api/guest/build/route.ts
import { NextResponse } from 'next/server';
import { startBuild, checkTokens } from '@/lib/buildService';

export async function POST(request: Request) {
  const { prompt, sessionId } = await request.json();
  const build = await startBuild(prompt, sessionId);
  return NextResponse.json(build);
}

export async function GET(request: Request) {
  const { searchParams } = new URL(request.url);
  const sessionId = searchParams.get('sessionId');
  const tokens = await checkTokens(sessionId);
  return NextResponse.json(tokens);
}

// src/lib/buildService.ts
let tokenStore = {};

export async function startBuild(prompt, sessionId) {
  // Logic to start the build process
  if (!tokenStore[sessionId] || tokenStore[sessionId] <= 0) {
    throw new Error('Insufficient tokens');
  }
  tokenStore[sessionId] -= 1; // Decrement token
  // Simulate build process
  const buildId = Math.random().toString(36).substring(2, 15);
  return { buildId, sessionId, tokens: tokenStore[sessionId] };
}

export async function checkTokens(sessionId) {
  return { sessionId, tokens: tokenStore[sessionId] || 0 };
}

// src/lib/tokenService.ts
export function initializeTokens(sessionId) {
  tokenStore[sessionId] = 5; // Initialize with 5 tokens
}

export function useToken(sessionId) {
  if (tokenStore[sessionId] > 0) {
    tokenStore[sessionId] -= 1;
  }
}

export function addTokens(sessionId, count) {
  tokenStore[sessionId] = (tokenStore[sessionId] || 0) + count;
}

