const HomePage = () => {
  const [prompt, setPrompt] = useState('');

  const handlePromptChange = (e) => {
    setPrompt(e.target.value);
  };

  const handleStartBuilding = () => {
    localStorage.setItem('olympus_initial_prompt', prompt);
    localStorage.setItem('olympus_tokens', '5');
    window.location.href = '/builder';
  };

  return (
    <div className='flex flex-col items-center justify-center h-screen'>
      <h1 className='text-5xl font-bold gradient-text'>Build anything. <span className='text-primary'>50X faster.</span></h1>
      <p className='mt-2'>AI-Powered  No Code Required  Deploy Instantly</p>
      <div className='bg-white/10 p-6 rounded-lg shadow-lg'>
        <Textarea 
          value={prompt}
          onChange={handlePromptChange}
          placeholder='Describe what you want to build...'
        />
        <Button onClick={handleStartBuilding} className='mt-4' variant='primary'>Start Building</Button>
      </div>
      <div className='mt-6'>
        <p>No signup required. Start now, save later.</p>
      </div>
    </div>
  );
};

