import { useEffect, useState } from 'react';
import { Button } from '@/components/ui';

const BuilderPage = () => {
  const [tokens, setTokens] = useState(5);

  useEffect(() => {
    const storedTokens = localStorage.getItem('olympus_tokens');
    if (storedTokens) {
      setTokens(parseInt(storedTokens, 10));
    }
  }, []);

  const handleChatSend = () => {
    if (tokens > 0) {
      // Logic for sending a message to the AI
      setTokens(tokens - 1);
