// buildService.ts
import { z } from 'zod';
import { db } from './db';

const buildRequestSchema = z.object({
  prompt: z.string(),
  sessionId: z.string(),
});

export const buildService = {
  async startBuild(prompt: string, sessionId: string) {
    const validatedInput = buildRequestSchema.parse({ prompt, sessionId });
    try {
      const build = await db.build.create({
        data: { prompt: validatedInput.prompt, sessionId: validatedInput.sessionId },
      });
      return build;
    } catch (error) {
      throw new Error('Failed to start build: ' + error.message);
    }
  },

  async checkTokens(sessionId: string) {
    try {
      const session = await db.session.findUnique({
        where: { id: sessionId },
      });
      if (!session) {
        throw new Error('Session not found');
      }
      return { sessionId: session.id, tokens: session.tokens };
    } catch (error) {
      throw new Error('Failed to check tokens: ' + error.message);
    }
  },
};

// tokenService.ts
export const tokenService = {
  decrementToken(tokens: number): number {
    if (tokens <= 0) {
      throw new Error('No tokens available');
    }
    return tokens - 1;
  },

  resetTokens(): number {
    return 5; // Reset to initial tokens
  },

  addTokens(currentTokens: number, additionalTokens: number): number {
    return currentTokens + additionalTokens;
  },
};

