// Repository pattern implementation

import { PrismaClient } from '@prisma/client';
import { Project, User } from './models';
import { cache } from './cache';

const prisma = new PrismaClient();

export class Repository {
  async getUserById(id: string): Promise<User | null> {
    const cacheKey = `user_${id}`;
    const cachedUser = cache.get(cacheKey);
    if (cachedUser) return cachedUser;

    const user = await prisma.user.findUnique({ where: { id } });
    if (user) cache.set(cacheKey, user);
    return user;
  }

  async createUser(data: User): Promise<User> {
    const user = await prisma.user.create({ data });
    cache.invalidate(`user_${user.id}`);
    return user;
  }

  async getProjectById(id: string): Promise<Project | null> {
    const cacheKey = `project_${id}`;
    const cachedProject = cache.get(cacheKey);
    if (cachedProject) return cachedProject;

    const project = await prisma.project.findUnique({ where: { id } });
    if (project) cache.set(cacheKey, project);
    return project;
  }

  async createProject(data: Project): Promise<Project> {
    const project = await prisma.project.create({ data });
    cache.invalidate(`project_${project.id}`);
    return project;
  }

  async softDeleteUser(id: string): Promise<void> {
    await prisma.user.update({
      where: { id },
      data: { deletedAt: new Date() },
    });
    cache.invalidate(`user_${id}`);
  }

  async softDeleteProject(id: string): Promise<void> {
    await prisma.project.update({
      where: { id },
      data: { deletedAt: new Date() },
    });
    cache.invalidate(`project_${id}`);
  }
}

class Cache {
  private store: { [key: string]: any } = {};

  get(key: string): any {
    return this.store[key];
  }

  set(key: string, value: any): void {
    this.store[key] = value;
  }

  invalidate(key: string): void {
    delete this.store[key];
  }
}

export const cache = new Cache();

import { PrismaClient } from '@prisma/client';

const prisma = new PrismaClient();

export const connectDb = async () => {
  try {
    await prisma.$connect();
    console.log('Database connected successfully');
  } catch (error) {
    console.error('Database connection failed:', error);
    process.exit(1);
  }
};

export const disconnectDb = async () => {
  await prisma.$disconnect();
};

