// API client for interacting with the backend
import axios from 'axios';

const apiClient = axios.create({
  baseURL: '/api/v1',
  headers: {
    'Content-Type': 'application/json',
  },
});

export const buildApi = {
  startBuild: async (prompt, sessionId) => {
    const response = await apiClient.post('/guest/build', { prompt, sessionId });
    return response.data;
  },
  checkTokens: async (sessionId) => {
    const response = await apiClient.get(`/guest/build?sessionId=${sessionId}`);
    return response.data;
  },
};

// Hook for managing build process and tokens
import { useState, useEffect } from 'react';
import { buildApi } from '../lib/api';

export const useBuild = (initialPrompt) => {
  const [tokens, setTokens] = useState(5);
  const [sessionId, setSessionId] = useState('');
  const [buildId, setBuildId] = useState('');
  const [status, setStatus] = useState('');

  const startBuild = async () => {
    const { buildId, sessionId, tokens } = await buildApi.startBuild(initialPrompt, sessionId);
    setBuildId(buildId);
    setSessionId(sessionId);
    setTokens(tokens);
    setStatus('Building...');
  };

  const checkTokens = async () => {
    const { tokens } = await buildApi.checkTokens(sessionId);
    setTokens(tokens);
  };

  useEffect(() => {
    if (sessionId) {
      const interval = setInterval(checkTokens, 5000);
      return () => clearInterval(interval);
    }
  }, [sessionId]);

  return { tokens, startBuild, buildId, status };
};

// Hook for optimistic updates
import { useState } from 'react';

export const useOptimisticUpdate = (initialValue) => {
  const [value, setValue] = useState(initialValue);

  const optimisticUpdate = (newValue) => {
    const previousValue = value;
    setValue(newValue);
    return () => setValue(previousValue);
  };

  return { value, optimisticUpdate };
};

// Hook for caching
import { useEffect } from 'react';

export const useCache = (key, data) => {
  useEffect(() => {
    localStorage.setItem(key, JSON.stringify(data));
  }, [key, data]);
};

