      "content": "// Hook for managing build process and tokens
import { useState, useEffect } from 'react';
import { buildApi } from '../lib/api';

export const useBuild = (initialPrompt) => {
  const [tokens, setTokens] = useState(5);
  const [sessionId, setSessionId] = useState('');
  const [buildId, setBuildId] = useState('');
  const [status, setStatus] = useState('');

  const startBuild = async () => {
    const { buildId, sessionId, tokens } = await buildApi.startBuild(initialPrompt, sessionId);
    setBuildId(buildId);
    setSessionId(sessionId);
    setTokens(tokens);
    setStatus('Building...');
  };

  const checkTokens = async () => {
    const { tokens } = await buildApi.checkTokens(sessionId);
    setTokens(tokens);
  };

  useEffect(() => {
    if (sessionId) {
      const interval = setInterval(checkTokens, 5000);
      return () => clearInterval(interval);
    }
  }, [sessionId]);

  return { tokens, startBuild, buildId, status };
};
