const input = $input.first().json;
const body = input.body || input;

// Extract parameters with defaults
const prompt = body.prompt?.trim();
const type = body.type || 'custom';
const style = body.style || 'none';
const width = Math.min(Math.max(body.width || 1024, 256), 2048);
const height = Math.min(Math.max(body.height || 768, 256), 2048);
const seed = body.seed || Math.floor(Math.random() * 999999999);
const enhance = body.enhance !== false;
const negative = body.negative || '';
const model = body.model || 'flux';  // flux, turbo, flux-realism, flux-anime, flux-3d
const nologo = body.nologo !== false;

// Validation
const errors = [];

if (!prompt) {
  errors.push('prompt is required');
}

if (prompt && prompt.length > 2000) {
  errors.push('prompt must be under 2000 characters');
}

const validTypes = ['hero', 'icon', 'background', 'illustration', 'avatar', 'product', 'logo', 'banner', 'thumbnail', 'custom'];
if (!validTypes.includes(type)) {
  errors.push(`type must be one of: ${validTypes.join(', ')}`);
}

const validStyles = ['none', 'cinematic', 'anime', 'photorealistic', 'digital-art', 'oil-painting', 'watercolor', '3d-render', 'pixel-art', 'comic', 'minimalist', 'vintage', 'neon', 'cyberpunk', 'fantasy', 'steampunk'];
if (!validStyles.includes(style)) {
  errors.push(`style must be one of: ${validStyles.join(', ')}`);
}

const validModels = ['flux', 'turbo', 'flux-realism', 'flux-anime', 'flux-3d'];
if (!validModels.includes(model)) {
  errors.push(`model must be one of: ${validModels.join(', ')}`);
}

return {
  valid: errors.length === 0,
  errors,
  data: {
    prompt,
    type,
    style,
    width,
    height,
    seed,
    enhance,
    negative,
    model,
    nologo
  },
  timestamp: new Date().toISOString(),
  requestId: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

