        import React, { useState } from 'react';

        const SettingsPanel = () => {
          const [darkMode, setDarkMode] = useState(false);
          const [notifications, setNotifications] = useState(true);

          const handleToggleDarkMode = () => {
            setDarkMode(!darkMode);
          };

          const handleToggleNotifications = () => {
            setNotifications(!notifications);
          };

          return (
            <div className=\"container mx-auto p-4\">
              <h2 className=\"text-xl font-bold mb-4\">Settings</h2>
              <div className=\"flex flex-wrap -mx-4\">
                <div className=\"w-full md:w-1/2 xl:w-1/3 p-4\">
                  <div className=\"bg-white rounded shadow-md p-4\">
                    <h3 className=\"text-lg font-bold mb-2\">Dark Mode</h3>
                    <div className=\"flex justify-end gap-2\">
                      <button
                        className={`bg-${darkMode ? 'green' : 'red'}-500 hover:bg-${darkMode ? 'green' : 'red'}-700 text-white font-bold py-2 px-4 rounded`}
                        onClick={handleToggleDarkMode}
                      >
                        {darkMode ? 'Enabled' : 'Disabled'}
                      </button>
                    </div>
                  </div>
                </div>
                <div className=\"w-full md:w-1/2 xl:w-1/3 p-4\">
                  <div className=\"bg-white rounded shadow-md p-4\">
                    <h3 className=\"text-lg font-bold mb-2\">Notifications</h3>
                    <div className=\"flex justify-end gap-2\">
                      <button
                        className={`bg-${notifications ? 'green' : 'red'}-500 hover:bg-${notifications ? 'green' : 'red'}-700 text-white font-bold py-2 px-4 rounded`}
                        onClick={handleToggleNotifications}
                      >
                        {notifications ? 'Enabled' : 'Disabled'}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          );
        };

        export default SettingsPanel;
